maze.h
=====================================================
#ifndef MAZE_H
#define MAZE_H
#include
#include
	#define MAX_MAZE_SIZE 100
#define MAX_STACK_SIZE 500
	extern int Number;
	typedef enum{
Wall,
Space,
Thinked
}TMazeElem;
	typedef struct{/*坐标*/
int x,y;
}Coordinate;
	/*用一个数组来表示迷宫*/
typedef struct {
int iLength,iHeight;/*迷宫的长度和宽度*/
TMazeElem pMaze[MAX_MAZE_SIZE][MAX_MAZE_SIZE];
Coordinate in,out;/*迷宫的入口和出口*/
}TMaze;
	typedef struct{/*定义迷宫解的路径上的元素*/
Coordinate pos;/*当前位置*/
int di;/*下一个应该探索的方向*/
}PathElem;
	/*定义在迷宫上的操作*/
	void PrintMaze(TMaze *maze);
/*输出迷宫到屏幕*/
	bool LoadMaze(TMaze *maze,char *fname,char *errMsg);
/*从文件中输入迷宫*/
	bool SaveMaze(TMaze *maze,char *fname);
/*保存到文件fname迷宫*/
	void ClearMaze(TMaze *);
/*清空迷宫*/
	void FindMazePath_R(TMaze *maze,Coordinate cur,char *fname);
/*查找迷宫的解，并且把迷宫的接写入文件fname,cur当前位置
*用递归实现
*没有调试信息
*/
	void FindMazePath_RD(TMaze *maze,Coordinate cur,char *fname,FILE *d);
/*查找迷宫的解，并且把迷宫的接写入文件fname
*用递归实现
*带调试信息,调试信息被写入文件d，通过该文件你可以了解程序是怎样探索迷宫的
*/
	void FindMazePath_B(TMaze *maze,char *fname);
/*用回溯法探索迷宫的所有解，并且把解写入文件名为fname 的文件
*
*用回溯法探索迷宫的解和用堆栈实际上是一致的，在这里没有用堆栈是因为：
*如果用堆栈的话有要增加一个头文件来实现堆栈的基本操作，文件越多就越不
*便于调试，因此在这里我用了一个数组来代替堆栈，但是实际上都一样
*
*不输出调试信息，不输出迷宫的探索过程
*/
	void FindMazePath_BD(TMaze *maze,char *fname,char *dname);
/*用回溯法探索迷宫的所有解，并且把解写入文件名为fname 的文件
*
*用回溯法探索迷宫的解和用堆栈实际上是一致的，在这里没有用堆栈是因为：
*如果用堆栈的话有要增加一个头文件来实现堆栈的基本操作，文件越多就越不
*便于调试，因此在这里我用了一个数组来代替堆栈，但是实际上都一样
*
*带调试信息,调试信息被写入文件dname，通过该文件你可以了解程序是这样探索迷宫的
*/
	#endif
